			if ($status->lunas==1) $dt['lunas'] = 1;
			
			
			if (in_array(strtolower($username),$amnesty)) $dt['lunas'] = 1;
			
			if($dt['dokumen']==0){
				$dt['revisi'] = 0;
			}
